import React, { useState } from "react";
import Axios from "axios";
import '../hoja-de-estilos/IngresoDatos.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function GuardarDatos(){
    const [nombre, setNombre]=useState("");
    const [edad, setEdad]=useState(0);
    const [pais, setPais]=useState("");
    const [cargo, setCargo]=useState("");
    const [anio, setAnios]=useState(0);

    const [empleadosList,setEmpleados] =useState([]);

    const mostrarDatos = () =>{
      Axios.post("http://localhost:3001/create", {
        nombre:nombre,
        edad:edad,
        pais:pais,
        cargo:cargo,
        anio:anio
      }).then(()=>{
        alert("empresa registrado");
      });
    }

    const getEmpleados = () =>{
        Axios.get("http://localhost:3001/read").then((response)=>{
          setEmpleados(response.data);        
        });
    }
  

    return (
        <div className="App">
            <div className='datos'>
                <div className="card text-center">
                    <div className="card header">
                        <h4>Guardar datos</h4>
                    </div>
                    
                    <div className="mb-3 row">
                        <label>Nombre: <input onChange={(event) =>{setNombre(event.target.value);}} type='text'/></label>
                        <label>Edad: <input onChange={(event) =>{setEdad(event.target.value);}} type='number'/></label>
                        <label>Pais: <input onChange={(event) =>{setPais(event.target.value);}} type='text'/></label>
                        <label>Cargo: <input onChange={(event) =>{setCargo(event.target.value);}} type='text'/></label>
                        <label>AÃ±o: <input onChange={(event) =>{setAnios(event.target.value);}} type='number'/></label>  
                    </div>
                    <button className="btn btn-success" onClick={mostrarDatos}>REGISTRAR</button>
                </div>
                <div className='listar'>
                    <button onClick={getEmpleados}>LISTAR</button>
                    {
                        empleadosList.map((val,key)=>{
                            return <tr>
                                <th scope="row">{val.id}</th>
                                <td>{val.nombre} </td>
                                <td>{val.edad} </td>
                                <td>{val.pais} </td>
                                <td>{val.cargo} </td>
                                <td>{val.anio} </td>
                                <td>
                                    <button type="button" className="btn btn-info">Editar</button>
                                    <button type="button" className="btn btn-danger">Eliminar</button>
                                </td>
                            </tr>
                        })
                    }
                </div>
            </div>
        </div>
    );
};

export default GuardarDatos;
